void thomas_algorithm( const std::vector&lt;double&gt; &amp; a,
    const std::vector&lt;double&gt; &amp; b,
    const std::vector&lt;double&gt; &amp; c,
    const std::vector&lt;double&gt; &amp; d,
    std::vector&lt;double&gt; &amp; x )
{
    size_t N = d.size();

    std::vector&lt;double&gt; c_star( N, 0.0 );
    std::vector&lt;double&gt; d_star( N, 0.0 );

    c_star[ 0 ] = c[ 0 ] / b[ 0 ];
    d_star[ 0 ] = d[ 0 ] / b[ 0 ];

    for ( int i = 1; i &lt; N; ++ i )
    {
        double coef = 1.0 / ( b[ i ] - a[ i ] * c_star[ i - 1 ] );
        c_star[ i ] = c[ i ] * coef;
        d_star[ i ] = ( d[ i ] - a[ i ] * d_star[ i - 1 ] ) * coef;
    }

    x[ N - 1 ] = d_star[ N - 1 ];

    for ( int i = N - 2; i &gt;= 0; -- i )
    {
        x[ i ] = d_star[ i ] - c_star[ i ] * x[ i + 1 ];
    }
}

void split(
    std::vector&lt;double&gt; &amp; a,
    std::vector&lt;double&gt; &amp; b,
    std::vector&lt;double&gt; &amp; c,
    std::vector&lt;double&gt; &amp; y,
    std::vector&lt;double&gt; &amp; a_bar,
    std::vector&lt;double&gt; &amp; b_bar,
    std::vector&lt;double&gt; &amp; c_bar,
    std::vector&lt;double&gt; &amp; y_bar )
{
    int N = a.size();
    a_bar.resize( N );
    b_bar.resize( N );
    c_bar.resize( N );
    y_bar.resize( N );
    for ( int i = 0; i &lt; N; ++ i )
    {
        double bim1 = 0;
        double bip1 = 0;
        double aim1 = 0;
        double aip1 = 0;
        double cim1 = 0;
        double cip1 = 0;
        double yim1 = 0;
        double yip1 = 0;
        double alpha = 0;
        double beta = 0;
        if ( i != 0 )
        {
            aim1 =  a[ i - 1 ];
            bim1 =  b[ i - 1 ];
            cim1 =  c[ i - 1 ];
            yim1 =  y[ i - 1 ];
            alpha = - a[ i ] / bim1;
        }

        if ( i != N - 1 )
        {
            aip1 =  a[ i + 1 ];
            bip1 =  b[ i + 1 ];
            cip1 =  c[ i + 1 ];
            yip1 =  y[ i + 1 ];
            beta = - c[ i ] / bip1;
        }
        a_bar[ i ] = alpha * aim1;
        c_bar[ i ] = beta * cip1;
        b_bar[ i ] = b[ i ] + alpha * cim1 + beta * aip1;
        y_bar[ i ] = y[ i ] + alpha * yim1 + beta * yip1;
    }
}

void SetCyclicReductionValue
(
    std::vector&lt;double&gt; &amp; a,
    std::vector&lt;double&gt; &amp; b,
    std::vector&lt;double&gt; &amp; c,
    std::vector&lt;double&gt; &amp; y,
    std::vector&lt;double&gt; &amp; a1,
    std::vector&lt;double&gt; &amp; b1,
    std::vector&lt;double&gt; &amp; c1,
    std::vector&lt;double&gt; &amp; y1,
    std::vector&lt;double&gt; &amp; a2,
    std::vector&lt;double&gt; &amp; b2,
    std::vector&lt;double&gt; &amp; c2,
    std::vector&lt;double&gt; &amp; y2
)
{
    int N = a.size();
    int halfN = N / 2;
    a1.resize( halfN );
    b1.resize( halfN );
    c1.resize( halfN );
    y1.resize( halfN );
    a2.resize( halfN );
    b2.resize( halfN );
    c2.resize( halfN );
    y2.resize( halfN );
    for ( int i = 0; i &lt; halfN; ++ i )
    {
        int ieven = 2 * i;
        int iodd  = ieven + 1;
        a1[ i ] = a[ ieven ];
        b1[ i ] = b[ ieven ];
        c1[ i ] = c[ ieven ];
        y1[ i ] = y[ ieven ];

        a2[ i ] = a[ iodd ];
        b2[ i ] = b[ iodd ];
        c2[ i ] = c[ iodd ];
        y2[ i ] = y[ iodd ];
    }
}

int main( int argc, char ** argv )
{
