void boundary( int N, vec1d &amp; u )
{
    //left bc
    int i = 0;
    u[ i ] = 0.0;
    u[ i - 1 ] = 2.0 * u[ i ] - u[ i + 1 ]; 
    u[ i - 2 ] = 3.0 * u[ i ] - 2.0 * u[ i + 1 ];   
