void runge_kutta_stage3( int N, vec1d &amp; un, vec1d &amp; ut, vec1d &amp; res, double dx, double dt )
{
    rhs( N, dx, ut, res );

    for ( int i = 0; i &lt; N; ++ i )
    {
        un[ i ] = ( 1.0 / 3.0 ) * un[ i ] + ( 2.0 / 3.0 ) * ut[ i ] + ( 2.0 / 3.0 ) * dt * res[ i ];
    }
    boundary( N, un );
}
