</code></pre> <p>main.cpp C++<pre><code>#include &lt;png.h&gt;
#include &lt;cstdio&gt;
#include &lt;cstdlib&gt;
#include &lt;vector&gt;

void error_handler(png_structp png, png_const_charp msg) {
    std::fprintf(stderr, "libpng error: %s\n", msg);
    longjmp(png_jmpbuf(png), 1);
}

int main() {
    const int width = 64;
    const int height = 64;
    const int row_bytes = width * 4; // RGBA = 4 字节

    // 1. 打开输出文件
    FILE* fp = std::fopen("out.png", "wb");
    if (!fp) {
        std::perror("fopen");
        return 1;
    }
    // 测试文件可写性
    if (std::fputc(0, fp) == EOF) {
        std::perror("fputc");
        std::fclose(fp);
        fp = nullptr;
        return 1;
    }
    std::rewind(fp);

    // 2. 创建写结构 &amp; 信息结构
    png_structp png = png_create_write_struct(PNG_LIBPNG_VER_STRING, nullptr, error_handler, nullptr);
    if (!png) {
        std::fclose(fp);
        fp = nullptr;
        return 2;
    }

    png_infop info = png_create_info_struct(png);
    if (!info) {
        png_destroy_write_struct(&amp;png, nullptr);
        if (fp != nullptr) {
            std::fclose(fp);
            fp = nullptr;
        }
        return 3;
    }

    // 3. 设置错误跳转
    if (setjmp(png_jmpbuf(png))) {
        png_destroy_write_struct(&amp;png, &amp;info);
        if (fp != nullptr) {
            if (std::fclose(fp) != 0) {
                std::perror("fclose");
            }
            fp = nullptr;
        }
        return 4;
    }

    // 4. 绑定文件句柄
    png_init_io(png, fp);

    // 5. 写入文件头
    png_set_IHDR(png, info, width, height, 8, PNG_COLOR_TYPE_RGBA,
        PNG_INTERLACE_NONE, PNG_COMPRESSION_TYPE_DEFAULT, PNG_FILTER_TYPE_DEFAULT);
    png_write_info(png, info);

    // 6. 生成像素数据（纯红）并写入
    std::vector&lt;png_byte&gt; row(row_bytes);
    for (int x = 0; x &lt; width; ++x) {
        row[x * 4 + 0] = 0xFF; // R
        row[x * 4 + 1] = 0x00; // G
        row[x * 4 + 2] = 0x00; // B
        row[x * 4 + 3] = 0xFF; // A
    }
    for (int y = 0; y &lt; height; ++y) {
        png_write_row(png, row.data());
    }

    // 7. 结束写入
    png_write_end(png, nullptr);
    png_destroy_write_struct(&amp;png, &amp;info);
    if (fp != nullptr) {
        if (std::fclose(fp) != 0) {
            std::perror("fclose");
        }
        fp = nullptr;
    }
    return 0;
}
