        std::unique_lock lck( mtx );
        cv.wait( lck );
        std::cout &lt;&lt; "th1 is awake" &lt;&lt; std::endl;
        });
    std::thread th2( [&amp;]() {
        std::unique_lock lck( mtx );
        cv.wait( lck );
